$(document).ready(function() {
    $('.js-slider-block').slick({
        dots: true,
        arrows:true,
        infinite: true, 
        speed: 300,	  
        slidesToShow: 1,
        slidesToScroll: 1
      }); 
      $('.js-slider-block').on("afterChange", function(event, slick, currentSlide, nextSlide){
        $( '.welcom-slider-current' ).text('0' + (currentSlide + 1));
    });
    $( '.welcom-slider-count' ).text('0' + $('.js-slider-block').slick("getSlick").slideCount);   



    $('.lazy').slick({
        dots: true,
        arrows:true,
        lazyLoad: 'ondemand',
        slidesToShow: 3,
        slidesToScroll: 1
      });



    const gallery = document.querySelector('.explore-item-img');
    const galleryBefore = document.querySelector('.explore-item-before'); 
    const galleryBeforeScr = document.querySelector('.explore-item-before-scr'); 

    gallery.addEventListener('mousemove', (event) => { 
        let x = event.offsetX;
        galleryBeforeScr.style.width = x + 'px';
        galleryBefore.style.width = (x - 20) + 'px';
        
        
    })

 

});